import React from 'react';
import { connect } from 'react-redux';
import R from 'ramda';
import { Link } from 'react-router';

import { removePhoneFromCart, cartCheckout, cleanCart } from 'actions';
import { getTotalCartPrice, getCartPhonesWithCount } from 'selectors';

const CartPage = ({ phones, totalPrice, removePhoneFromCart, cartCheckout, cleanCart }) => {
  const isCartEmpty = R.isEmpty(phones);

  const renderContent = () => (
    <div>
      {isCartEmpty && <div>Your shopping cart is empty</div>}

      <div className="table-responsive">
        <table className="table-bordered table-striped table-condensed cf">
          <tbody>
            {phones.map((phone, index) => (
              <tr key={index} className="item-checkout">
                <td className="first-column-checkout">
                  <img className="img-thumbnail" src={phone.image} alt={phone.name} />
                </td>
                <td>{phone.name}</td>
                <td>{phone.price}</td>
                <td>{phone.count}</td>
                <td>
                  <span className="delete-cart" onClick={() => removePhoneFromCart(phone.id)} />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      {R.not(isCartEmpty) && (
        <div className="row">
          <div className="pull-right total-user-checkout">
            <b>Total: </b>${totalPrice}
          </div>
        </div>
      )}
    </div>
  );

  const renderSidebar = () => (
    <div>
      <Link to="/" className="btn btn-info">
        <span className="glyphicon glyphicon-info-sign" />
        <span>Continue shopping</span>
      </Link>
      {R.not(isCartEmpty) && (
        <div>
          <button className="btn btn-danger" onClick={cleanCart}>
            <span className="glyphicon glyphicon-trash">Clear Cart</span>
          </button>
          <button className="btn btn-success" onClick={() => cartCheckout(phones)}>
            <span className="glyphicon glypicon-envelope">Checkout</span>
          </button>
        </div>
      )}
    </div>
  );

  return (
    <div className="view-container">
      <div className="container">
        <div className="row">
          <div className="col-md-9">{renderContent()}</div>
          <div className="col-md-3 btn-user-checkout">{renderSidebar()}</div>
        </div>
      </div>
    </div>
  );
};

const mapStateToProps = state => ({
  phones: getCartPhonesWithCount(state),
  totalPrice: getTotalCartPrice(state),
});

const mapDispatchToProps = {
  removePhoneFromCart,
  cartCheckout,
  cleanCart,
};

export default connect(
  mapStateToProps,
  mapDispatchToProps,
)(CartPage);
