import R from 'ramda';

import { ADD_PHONE_TO_CART, REMOVE_PHONE_FROM_CART, CLEAN_CART } from 'actionTypes';

const initialState = [];

export default (state = initialState, { type, payload }) => {
  switch (type) {
    case ADD_PHONE_TO_CART:
      return R.append(payload, state);
    case REMOVE_PHONE_FROM_CART:
      return R.without(R.of(payload), state);
    case CLEAN_CART:
      return [];
    default:
      return state;
  }
};
